import React, { useState } from 'react';
import './App.scss';

function App() {

  const [deck, setDeck] = useState(52)
  const [lowThresh, setLowThresh] = useState(0)
  const [medThresh, setMedThresh] = useState(0)
  const [highThresh, setHighThresh] = useState(0)

  const incrementLow = () => {
    if (lowThresh < 20) {
      setLowThresh(lowThresh + 1)
      setDeck(deck - 1)}
  }

  const incrementMed = () => {
    if (medThresh < 12) {
      setMedThresh(medThresh + 1)
      setDeck(deck - 1)
    }
  }

  const incrementHigh = () => {
    if (highThresh < 20) {
      setHighThresh(highThresh + 1)
      setDeck(deck - 1)
    }
  }

  const reset = () => {
    setLowThresh(0)
    setMedThresh(0)
    setHighThresh(0)
    setDeck(52)
  }



  return (
    <div className="app">
      <h1>{deck} cards remaining</h1>
      <div className='thresh' onClick={() => incrementLow()}>
        <h3>Cards <span>2</span><span>3</span><span>4</span><span>5</span><span>6</span></h3>
        <h5>showing {lowThresh}/20 cards</h5>
        <h5>{Math.round((20  - lowThresh) / deck * 100)}% chance of flipping</h5>
      </div>
      <div className='thresh' onClick={() => incrementMed()}>
        <h3>Cards <span>7</span><span>8</span><span>9</span></h3>
        <h5>showing {medThresh}/12</h5>
        <h5>{Math.round((12  - medThresh) / deck * 100)}% chance of flipping</h5>
      </div>
      <div className='thresh' onClick={() => incrementHigh()} >
        <h3>Cards <span>10</span><span>J</span><span>Q</span><span>K</span><span>A</span></h3>
        <h5>showing {highThresh}/20</h5>
        <h5>{Math.round((20  - highThresh) / deck * 100)}% chance of flipping</h5>
      </div>
      <button onClick={() => reset()}>reset</button>
    </div>
  );
}

export default App;
