import React, { useState } from 'react';
import './App.css';

function App() {

  const [deck, setDeck] = useState(52)
  const [lowThresh, setLowThresh] = useState(0)
  const [medThresh, setMedThresh] = useState(0)
  const [highThresh, setHighThresh] = useState(0)

  const decrementDeck = () => {
    setDeck(deck - 1)
  }



  return (
    <div className="App-header">
      <h1>{deck}</h1>
      <h4>2-6{lowThresh}</h4>
      <h4>7-9{medThresh}</h4>
      <h4>10+{highThresh}</h4>
      <button onClick={() => decrementDeck()}>2 - 6</button>
      <button onClick={() => decrementDeck()}>6 - 9</button>
      <button onClick={() => decrementDeck()}>10 +</button>
    </div>
  );
}

export default App;
