import React, { useState } from 'react';
import './App.css';

function App() {

  const [deck, setDeck] = useState(52)
  const [lowThresh, setLowThresh] = useState(0)
  const [medThresh, setMedThresh] = useState(0)
  const [highThresh, setHighThresh] = useState(0)

  const incrementLow = () => {
    setLowThresh(lowThresh + 1)
    setDeck(deck - 1)
  }

  const incrementMed = () => {
    setMedThresh(medThresh + 1)
    setDeck(deck - 1)
  }

  const incrementHigh = () => {
    setHighThresh(highThresh + 1)
    setDeck(deck - 1)
  }



  return (
    <div className="App-header">
      <h1>{deck} cards remaining</h1>
      <div>
        <h3>cards ranging from 2 - 6</h3>
        <h4>showing {lowThresh}/20 cards</h4>
        <h3>{Math.round((20  - lowThresh) / deck * 100)}% chance of flipping</h3>
      </div>
      <div>
        <h4>showing {medThresh}/12 cards ranging from 7 - 9</h4>
        <h3>{Math.round((12  - medThresh) / deck * 100)}% chance of flipping</h3>
      </div>
      <div>
        <h4>showing {highThresh}/20 cards ranging from 10 + </h4>
        <h3>{Math.round((20  - highThresh) / deck * 100)}% chance of flipping</h3>
      </div>
      <button onClick={() => incrementLow()}>2 - 6</button>
      <button onClick={() => incrementMed()}>6 - 9</button>
      <button onClick={() => incrementHigh()}>10 +</button>
    </div>
  );
}

export default App;
