import React, { useState } from 'react';
import './App.css';

function App() {

  const [deck, setDeck] = useState(52)

  const decrementDeck = () => {
    setDeck(deck - 1)
  }

  return (
    <div className="App-header">
      <h1>{deck}</h1>
      <button onClick={() => decrementDeck()}>2 - 6</button>
      <button onClick={() => decrementDeck()}>6 - 9</button>
      <button onClick={() => decrementDeck()}>10 +</button>
    </div>
  );
}

export default App;
